import Input from "./components/input/Input";
import Button from "./components/button/Button";
import Form from "./components/form/Form";
import "./App.css";
import { useState } from "react";
import Card from "./components/card/Card";

function App() {
  const [user, setUser] = useState({ name: "", password: "" });
  const handleChange = (e) => {
    if (e.target.type === "text") {
      setUser({ ...user, name: e.target.value });
      console.log(user);
    } else if (e.target.type === "password") {
      setUser({ ...user, password: e.target.value });
      console.log(user);
    }
  };
  const handleClick = () => {
    if (user.name === "") {
      alert("Email boş bırakılamaz");
    } else if (user.password === "") {
      alert("Şifre boş bırakılamaz");
    } else {
      alert("Email: " + user.name + "  Şifre: " + user.password);
    }
  };

  const [product, setProduct] = useState([
    {
      id: 1,
      title: "LAPTOP",
      image: "https://picsum.photos/id/0/5000/3333",
      info: "ASUS 480GB SSD 4GB Ekran Kartı 16GB Ram",
      adet: 1,
    },
    {
      id: 1,
      title: "MASAÜSTÜ BİLGİSAYAR",
      image: "https://i.ytimg.com/vi/g2B8v6UzwGU/hqdefault.jpg?sqp=-oaymwEcCNACELwBSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLCvcDtTcWU8Kd5VnyEp-CwUkUIlDQ",
      info: "MONİTÖR DAHİL FİYAT PERFORMANS KRALI OYUNCU BİLGİSAYARI",
      adet: 1,
    },
    {
      id: 2,
      title: "MASAÜSTÜ BİLGİSAYAR",
      image: "https://i.ytimg.com/vi/P56O3uK93qQ/hqdefault.jpg?sqp=-oaymwEcCNACELwBSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLDv32wUZjcVOqQ3UTKc1YgZyiqZcg",
      info: "6999 TL F/P KRALI TOPLAMA OYUN BİLGİSAYARI ve OYUN TESTİ",
      adet: 1,
    },
    {
      id: 3,
      title: "ASUS LAPTOP",
      image: "https://i.ytimg.com/vi/C15ZrrUOjq4/hqdefault.jpg?sqp=-oaymwEcCNACELwBSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLBFo3avZ4OjsgN7JGPMIrqADVZUfw",
      info: "Sıfır ürün",
      adet: 1,
    },
    {
      id: 4,
      title: "ASUS LAPTOP",
      image: "https://picsum.photos/id/0/5000/3333",
      info: "Sıfır ürün",
      adet: 1,
    },
  ]);

  const [basket, setBasket] = useState([]);
  return (
    <div className="container">
      <h1>DONANIM ARŞİVİ ÜRÜNLERİ</h1>
      <div className="content">
        {product.map((item, index) => {
          return (
            <Card
              key={index}
              onclick={() => {
                const arr = [...basket];
                arr.push(item);
                setBasket(arr);
                console.log(basket);
              }}
              title={item.title}
              info={item.info}
              image={item.image}
            />
          );
        })}
      </div>
    </div>

    // <div className="App">
    //   <Form>
    //     <Input
    //       type={"text"}
    //       placeholder={"Email"}
    //       onChange={handleChange}
    //       value={user.name}
    //     />
    //     <Input
    //       type={"password"}
    //       placeholder={"Şifre"}
    //       onChange={handleChange}
    //       value={user.password}
    //     />
    //     <Button onClick={handleClick} />
    //   </Form>
    // </div>
  );
}

export default App;
